import React, { useEffect, useState} from 'react';
import { Box, Button, Stack, TextField, Typography } from '@mui/material';
import { exerciseOptions, fetchData } from '../utils/fetchData';
import HorizontalScrollbar from './HorizontalScrollbar';

const SearchExercises = ({ setExercises, bodyPart, setBodyPart}) => {
  const [search, setSearch] = useState('');
  const [bodyParts, setBodyParts] = useState([]);

  useEffect(() => {
    const fetchExercisesData = async () => {
      const bodyPartsData = await fetchData('https://exercisedb.p.rapidapi.com/exercises/bodyPartList', exerciseOptions);

      setBodyParts(['all', ...bodyPartsData]);
    };

    fetchExercisesData();
  }, []);

  const handleSearch = async () => {
    if(search) {
      //change to page 1
      const exercisesData = await fetchData('https://exercisedb.p.rapidapi.com/exercises?limit=1500', exerciseOptions); //this is from the url provided by RapidAPI in the code snippet part (Nodejs)
      //look for the exercises by name, target, equipment and body part.
      const searchedExercises = exercisesData.filter( 
      (item) => item.name.toLowerCase().includes(search)
      || item.target.toLowerCase().includes(search)
      || item.equipment.toLowerCase().includes(search)
      || item.bodyPart.toLowerCase().includes(search),
      );
      window.scrollTo({ top: 1800, left: 100, behavior: 'smooth' });

      setSearch('');
      setExercises(searchedExercises);
    }

  };

  return (
    <Stack alignItems='center' mt='37px' justifyContent='center' p='20px'>
        <Typography fontWeight={700} sx={{ fontSize: {
            lg: '44px',
            xs: '30px'}}}
            mb='49px' textAlign='center'>
          Awesome Exercises You <br /> Should Know
        </Typography>
        <Box position='relative' mb='72px'>
          <TextField
            sx={{
              input: { 
                fontWeight: '700', 
                border: 'none', 
                borderRadius:'4px'
              },
              width: { lg: '1170px', xs: '350px'},
              backgroundColor: '#FFF',
              borderRadius: '40px'
            }}
            height='76px'
            value={search}
            onChange={(e) =>  setSearch(e.target.value.toLowerCase())}
            placeholder='Search Exercises'
            type='text'
          />
          <Button className='search-btn'
            sx={{
              bgcolor: '#FF2625',
              color: '#FFF',
              textTransform: 'none',
              width: { lg: '175px', xs: '80px'},
              fontSize: { lg: '20px', xs: '14px'},
              height: '56px',
              position: 'absolute',
              right: '0'
            }}
            onClick={handleSearch}
            >
            Search
          </Button>
        </Box>
        <Box sx={{ position: 'relative', width: '100%', p: '20px'}}>
          <HorizontalScrollbar data={bodyParts} bodyParts bodyPart={bodyPart} setBodyPart={setBodyPart} />
        </Box>
    </Stack> 
  )
};

export default SearchExercises;
